INSERT INTO `assessments` (`id`, `question`, `tag`, `coding`, `choose`, `answer`, `label`, `created_at`, `updated_at`) VALUES
                                                                                                                           (1, 'What does PHP stand for?\r\n', 'PHP', NULL, 'a) PHP stands for Preprocessor Home Page\r\nb) PHP stands for Pretext Hypertext Processor\r\nc) PHP stands for Hypertext Preprocessor\r\nd) PHP stands for Personal Hyper Processor', 'C', 'easy', '2023-09-05 13:20:49', '2023-09-05 13:20:49'),
                                                                                                                           (2, 'Which of the following is the correct syntax to write a PHP code?\r\n', 'PHP', '', 'a) <?php ?>\r\nb) < php >\r\nc) < ? php ?>\r\nd) <? ?>', 'a', 'easy', '2023-09-05 13:20:49', '2023-09-05 13:20:49'),
                                                                                                                           (3, 'Which of the following is the correct way to add a comment in PHP code?\r\n', 'PHP', '', 'a) #\r\nb) //\r\nc) /* */\r\nd) All of the mentioned', 'd', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (4, 'What will be the output of the following PHP code?\r\n', 'PHP', '<?php\r\n$x = 10;\r\n$y = 20;\r\nif ($x > $y && 1||1)\r\n    print \"1000 PHP MCQ\" ;\r\nelse\r\n    print \"Welcome to Sanfoundry\";\r\n?>', 'a) no output\r\nb) Welcome to Sanfoundry\r\nc) 1000 PHP MCQ\r\nd) error', 'c', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (5, 'Which of the looping statements is/are supported by PHP?\r\n', 'PHP', 'i) for loop\r\nii) while loop\r\niii) do-while loop\r\niv) foreach loop', 'a) Only iv)\r\nb) i) and ii)\r\nc) i), ii) and iii)\r\nd) i), ii), iii) and iv)', 'd', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (6, 'Which version of PHP introduced the advanced concepts of OOP?\r\n', 'PHP', '', 'a) PHP 6\r\nb) PHP 4\r\nc) PHP 5\r\nd) PHP 5.3', 'c', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (7, 'Which variable is used to collect form data sent with both the GET and POST methods?\r\n', 'PHP', '', 'a) $_BOTH\r\nb) $REQUEST\r\nc) $_REQUEST\r\nd) $BOTH', 'c', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (8, 'If $a = 12 what will be returned when ($a == 12) ? 5 : 1 is executed?\r\n', 'PHP', '', 'a) 1\r\nb) 5\r\nc) 12\r\nd) Error', 'b', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (9, 'What will be the output of the following PHP code?\r\n', 'PHP', '   <?php\r\n    $num  = 1;\r\n    $num1 = 2;\r\n    print $num . \"+\". $num1;\r\n   ?>', 'a) 3\r\nb) 1+2\r\nc) 1.+.2\r\nd) Error', 'b', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (10, 'What will be the output of the following PHP code?\r\n', 'PHP', '<?php\r\n    $a = \"clue\";\r\n    $a .= \"get\";\r\n    echo \"$a\";\r\n    ?>', 'a) get\r\nb) true\r\nc) false\r\nd) clueget', 'd', 'easy', '2023-09-05 14:22:37', '2023-09-05 14:22:37'),
                                                                                                                           (11, 'What will be the output of the following PHP program?\r\n', 'PHP', '<?php\r\n    function multi($num)\r\n    {\r\n        if ($num == 3)\r\n            echo \"I Wonder\";\r\n        if ($num == 7)\r\n            echo \"Which One\";\r\n        if ($num == 8)\r\n            echo \"Is The\";\r\n        if ($num == 19)\r\n            echo \"Correct Answer\";\r\n    }\r\n    $can = stripos(\"I love php, I love php too!\",\"PHP\");\r\n    multi($can);\r\n?>', 'a) Correct Answer\r\nb) Is The\r\nc) I Wonder\r\nd) Which One', 'b', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (12, 'What will be the output of the following PHP code snippet?\r\n', 'PHP', '<?php\r\n    $url = \"phpmcq@sanfoundry.com\";\r\n    echo ltrim(strstr($url, \"@\"),\"@\");\r\n?>', 'a) phpmcq@sanfoundry.com\r\nb) php@sanfoundry.com\r\nc) phpmcq@\r\nd) sanfoundry.com', 'c', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (13, 'What will be the output of the following PHP code?\r\n', 'PHP', '<?php\r\n$fname = array(\"Peter\", \"Ben\", \"Joe\");\r\n$age = array(\"35\", \"37\", \"43\");\r\n$c = array_combine($age, $fname);\r\nprint_r($c);\r\n?>', 'a) Array (Peter Ben Joe)\r\nb) Array ([Peter] => 35 [Ben] => 37 [Joe] => 43)\r\nc) Array (35 37 43)\r\nd) Array ([35] => Peter [37] => Ben [43] => Joe)\r\n', 'd', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (14, 'What will be the output of the following PHP code?\r\n', 'PHP', '<?php\r\n$a1 = array(\"a\" => \"red\", \"b\" => \"green\", \"c\" => \"blue\", \"d\" => \"yellow\");\r\n$a2 = array(\"e\" => \"red\", \"f\" => \"green\", \"g\" => \"blue\", \"h\" => \"orange\");\r\n$a3 = array(\"i\" => \"orange\");\r\n$a4 = array_merge($a2, $a3);\r\n$result = array_diff($a1, $a4);\r\nprint_r($result);\r\n?>', 'a) Array ( [d] => yellow )\r\nb) Array ( [i] => orange )\r\nc) Array ( [h] => orange )\r\nd) Array ( [d] => yellow [h] => orange )', 'a', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (15, 'Which one of the following property scopes is not supported by PHP?\r\n', 'PHP', NULL, 'a) friendly\r\nb) final\r\nc) public\r\nd) static', 'a', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (16, 'Which one of the following can be used to instantiate an object in PHP assuming class name to be Foo?\r\n', 'PHP', NULL, 'a) $obj = new $foo;\r\nb) $obj = new foo;\r\nc) $obj = new foo ();\r\nd) obj = new foo ();', 'b', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (17, 'Which of the following term originates from the Greek language that means “having multiple forms,” defines OOP’s ability to redefine, a class’s characteristics?', 'PHP', NULL, 'a) Abstraction\r\nb) Polymorphism\r\nc) Inheritance\r\nd) Differential', 'b', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (18, 'What is the description of Error level E_ERROR?\r\n', 'PHP', NULL, 'a) Fatal run-time error\r\nb) Near-fatal error\r\nc) Compile-time error\r\nd) Fatal Compile-time error', 'a', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (19, 'Which one of the following methods can be used to diagnose and display information about a MySQL connection error?', 'PHP', NULL, 'a) connect_errno()\r\nb) connect_error()\r\nc) mysqli_connect_errno()\r\nd) mysqli_connect_error()', 'd', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (20, 'Which one of the following databases has PHP supported almost since the beginning?', 'PHP', NULL, 'a) Oracle Database\r\nb) SQL\r\nc) SQL+\r\nd) MySQL', 'd', 'intermediate', '2023-09-05 14:32:58', '2023-09-05 14:32:58'),
                                                                                                                           (21, 'Which one of the following is the right way to clone an object?', 'PHP', NULL, 'a) _clone(targetObject);\r\nb) destinationObject = clone targetObject;\r\nc) destinationObject = _clone(targetObject);\r\nd) destinationObject = clone(targetObject);', 'd', 'hard', '2023-09-05 14:43:57', '2023-09-05 14:43:57'),
                                                                                                                           (22, 'Which magic method is used to implement overloading in PHP?\r\n', 'PHP', NULL, 'a) __call\r\nb) __invoke\r\nc) __wakeup\r\nd) __unset', 'a', 'hard', '2023-09-05 14:43:57', '2023-09-05 14:43:57'),
                                                                                                                           (23, 'What will be the output of the following PHP code?', 'PHP', '<?php\r\n    $title = \"O\malley wins the heavyweight championship!\";\r\n    echo ucwords($title);\r\n    ?>', 'a) O’Malley Wins The Heavyweight Championship!\r\nb) O’malley Wins The Heavyweight Championship!\r\nc) O’Malley wins the heavyweight championship!\r\nd) o’malley wins the heavyweight championship!', 'a', 'hard', '2023-09-05 15:04:20', '2023-09-05 15:04:20'),
(24, 'What will be the output of the following PHP code?', 'PHP', '<?php\r\n    $var=300;\r\n    $int_options = array(\"options\"=>array (\"min_range\"=>0, \"max_range\"=>256));\r\n    if (!filter_var($var, FILTER_VALIDATE_INT, $int_options))\r\n        echo(\"Integer is not valid\");\r\n    else\r\n        echo(\"Integer is valid\");\r\n    ?>', 'a) No output is returned\r\nb) Integer is not valid\r\nc) Integer is valid\r\nd) Error\r\n', 'b', 'hard', '2023-09-05 15:04:20', '2023-09-05 15:04:20'),
                                                                                                                            (25, 'Which one of the following PHP statements is true?\r\n', 'PHP', 'class CopyMe {}\r\n$first = new CopyMe();\r\n$second = $first;', 'a) In PHP 4: $second and $first are 2 distinct objects\r\nb) In PHP 5: $second and $first are 2 distinct objects\r\nc) In PHP 4: $second and $first refer to one object\r\nd) None of the mentioned', 'c', 'hard', '2023-09-05 15:04:20', '2023-09-05 15:04:20'),
                                                                                                                            (26, 'What will be the output of the following PHP code?\r\n', 'PHP', 'class Person \r\n{\r\n    function getName() { return \"Bob\"; }\r\n    function getAge() { return 44; }\r\n    function __toString() {\r\n        $desc = $this->getName();\r\n        $desc .= \" (age \".$this->getAge().\")\";\r\n        return $desc;\r\n    }\r\n}\r\n$person = new Person();\r\nprint $person;', 'a) Object Not Found\r\nb) PHP Catchable fatal error\r\nc) BOB (age 44)\r\nd) BOB', 'c', 'hard', '2023-09-05 15:04:20', '2023-09-05 15:04:20'),
